import './App.css';
import Pokemons from './components/PokemonsObj';


function App() {
  return (
    <body>
      { Pokemons.map(({ id, name, type, averageWeight, image}) => (
        <div key={id} className="pokemonsBox">
            <div>
              <h1>{ name }</h1>
              <h2>{ type }</h2>
              <h3>{ `${averageWeight.value}${averageWeight.measurementUnit}` }</h3>
              <img src={ image } alt={ name } />
            </div>
        </div>
      )
      ) }
  </body>
  );
}

export default App;
